export class TranscriptionSegment {
    index: number;
    start: number;
    duration: number;
    file: string;
    transcription: string;

    constructor(index: number, start: number, duration: number, file: string) {
        this.index = index;
        this.start = start;
        this.duration = duration;
        this.file = file;
    }

    get end(): number {
        return this.start + this.duration;
    }
}

export class SubtitleSegment {
    start: number;
    end: number;
    transcription: string;

    constructor(start: number, end: number, transcription: string) {

        this.start = start;
        this.end = end;
        this.transcription = transcription;
    }

}